mapper.sql:
FROM ccg a
WHERE 1=1
	AND DelNY = #{shDelNY}

		AND seq = #{shValue}
		AND ccg_name LIKE CONCAT('%',#{shValue},'%')

mapper.insert:

INSERT INTO ccg (
    ccg_name
    ,useNY
    ,delNY
)
VALUES (
	#{ccg_name}    
    ,#{useNY}
    ,#{delNY}
)
;

	SELECT last_insert_id()

mapper.update:
UPDATE ccg
SET
	ccg_name = #{ccg_name}
	,useNY = #{useNY}
	,delNY = #{delNY}

WHERE
	seq = #{seq}
