mapper.insert:
INSERT INTO crew (
	creator
	,crewName
	,sports
	,location
	,crewMemberNum
	,playDate
	,startTime
	,endTime
	,detail
	,locationDetail
)
VALUES (
	#{mmSeq}
	,#{crewName}
	,#{sports}
	,#{location}
	,#{crewMemberNum}
	,#{playDate}
	,#{startTime}
	,#{endTime}
	,#{detail}
	,#{locationDetail}
)

	SELECT last_insert_id()

mapper.delete:
DELETE
from crMember
where 1=1
	and crSeq = #{seq}
    and mmSeq = #{mmSeq}
    and crLeaderNy = 0
